fn basics(#[case] json: &str) -> Result<()> {
    let scenario = Scenario::from_json(json)?;
    let (result, final_state) = scenario.run();
    if !result {
        println!("{scenario}");
        if let Some(final_state) = final_state {
            println!("Actual:\n{final_state}");
        } else {
            println!("Actual: (not available)");
        }
    }
    assert!(result);
    Ok(())
}
